import './App.css';
import { useState, useEffect } from 'react';
import DataList from './components/dataList.jsx'
import Form from './components/Form.jsx'


function App() {

  const [modal, setModal] = useState(false)
  const [data, setData] = useState([])
  const [editUserID, setEditUserID] = useState(-1)

  const fetchUsers = () => {
    fetch("http://localhost:8800/")
      .then((response) => response.json())
      .then((data) => {
        setData(data);
      })
      .catch((error) => console.error("Erro ao buscar usuários:", error));
  };

  useEffect(() => {
    fetchUsers();
  }, []);


  function toggleModal(userId) {
    setModal(!modal)
    setEditUserID(userId)
    if (!modal) {
      fetchUsers(); // Atualiza a lista quando fechar o modal
    }
  }

  return (
    <div className="App">
      <h1>Lista de Usuários</h1>
        <button className="add-btn" type="button" onClick={() => toggleModal(-1)}>Adicionar</button>
      <DataList data={data} handleModal={toggleModal} onUserDeleted={fetchUsers} />
      {modal && <Form userid={editUserID} onSubmitSuccess={fetchUsers}/>}

    </div>
  );
}

export default App;
